function check() {
  const posts = document.getElementById("article_price").value
  postsA = Array.from(posts);

  postsA.forEach(function (post){ 
    post.addEventListener("post",(e) => { 
      const postID = post.getElementById("article_price");
      const XHR = new XMLHttpRequest();
      XHR.open("GET", `/posts/${postID}`, true);
      XHR.responseType = "json";
      XHR.send();
    });
  } );
}
window.addEventListener("lord" , check);
